pragma solidity 0.8.19;

enum ExtraModule {
    None,
    Echo, // for debugging
    Pyth
}

struct PythOffchainPrice {
    uint256 publishTime;
    int64 price;
    int32 expo;
    bytes vaa;
}

interface IMate2Automation1_1 {
    event catchErr(string _name, string _err);
    /**
     * @notice method that is simulated by the keepers to see if any work actually
     * needs to be performed. This method does does not actually need to be
     * executable, and since it is only ever simulated it can consume lots of gas.
     * @dev To ensure that it is never called, you may want to add the
     * cannotExecute modifier from KeeperBase to your implementation of this
     * method.
     * @param checkData specified in the upkeep registration so it is always the
     * same for a registered upkeep. This can easily be broken down into specific
     * arguments using `abi.decode`, so multiple upkeeps can be registered on the
     * same contract and easily differentiated by the contract.
     * @param extraData passed by keeper for passing offchain data
     * @return upkeepNeeded boolean to indicate whether the keeper should call
     * performUpkeep or not.
     * @return performData bytes that the keeper should call performUpkeep with, if
     * upkeep is needed. If you would like to encode data to decode later, try
     * `abi.encode`.
     */
    function checkUpkeep(
        bytes calldata checkData,
        bytes calldata extraData
    ) external view returns (bool upkeepNeeded, bytes memory performData);

    /**
     * @notice method that is actually executed by the keepers, via the registry.
     * The data returned by the checkUpkeep simulation will be passed into
     * this method to actually be executed.
     * @dev The input to this method should not be trusted, and the caller of the
     * method should not even be restricted to any single registry. Anyone should
     * be able call it, and the input should be validated, there is no guarantee
     * that the data passed in is the performData returned from checkUpkeep. This
     * could happen due to malicious keepers, racing keepers, or simply a state
     * change while the performUpkeep transaction is waiting for confirmation.
     * Always validate the data passed in.
     * @param performData is the data which was passed back from the checkData
     * simulation. If it is encoded, it can easily be decoded into other types by
     * calling `abi.decode`. This data should not be trusted, and should be
     * validated against the contract's current state.
     */
    function performUpkeep(bytes calldata performData) external;
}
","keccak256":"0xb89e4441dccc50c17d4113fdf6847ccc159e947badef3a9aafe77218b2e8d7c7","license":"MIT"},"@chromatic-protocol/contracts/core/automation/mate2/IMate2AutomationRegistry1_1.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import {ExtraModule} from "./IMate2Automation1_1.sol";

struct ExtraData {
    ExtraModule extraModule;
    bytes extraParam;
}

interface IMate2AutomationRegistry1_1 {
    // function getUpkeepAdmin(address target) external returns (address admin);
    function registerUpkeep(
        address target,
        uint32 gasLimit,
        address admin,
        bool useTreasury,
        bool singleExec,
        bytes calldata checkData,
        ExtraModule extraModule,
        bytes calldata extraParam
    ) external returns (uint256 id);

    function cancelUpkeep(uint256 id) external;

    function pauseUpkeep(uint256 id) external;

    function unpauseUpkeep(uint256 id) external;

    function transferUpkeepAdmin(uint256 id, address proposed) external;

    function updateCheckData(uint256 id, bytes calldata newCheckData) external;

    function getUpkeep(
        uint256 id
    )
        external
        view
        returns (
            address target,
            uint32 executeGas,
            bytes memory checkData,
            ExtraData memory extraData, // to avoid stack too deep
            address lastKeeper,
            address admin,
            uint64 maxValidBlocknumber,
            uint256 amountSpent,
            bool[3] memory flags // [bool useTreasury, bool singleExec, bool paused]
        );

    function getUpkeepTreasury() external view returns (address);

    function checkUpkeep(
        uint256 upkeepId,
        address from,
        bytes calldata extraData
    ) external view returns (bytes memory performData, uint256 maxPayment, uint256 gasLimit);

    function getPerformUpkeepFee() external view returns (uint256 fee);

    function addWhitelistedRegistrar(address registrar) external;

    function removeWhitelistedRegistrar(address registrar) external;
}
